INSERT INTO `va_wc_admin_notes` VALUES (1,'wc-admin-welcome-note','info','en_US','New feature(s)','Welcome to the new WooCommerce experience! In this new release you\'ll be able to have a glimpse of how your store is doing in the Dashboard, manage important aspects of your business (such as managing orders, stock, reviews) from anywhere in the interface, dive into your store data with a completely new Analytics section and more!','info','{}','unactioned','woocommerce-admin','2019-09-24 17:42:57',NULL,0),(2,'wc-admin-wc-helper-connection','info','en_US','Connect to WooCommerce.com','Connect to get important product notifications and updates.','info','{}','unactioned','woocommerce-admin','2019-09-24 17:42:58',NULL,0),(3,'wc-admin-mobile-app','info','en_US','Install Woo mobile app','Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.','phone','{}','unactioned','woocommerce-admin','2019-09-26 19:43:16',NULL,0),(4,'wc-admin-store-notice-giving-feedback','info','en_US','Review your experience','If you like WooCommerce Admin please leave us a 5 star rating. A huge thanks in advance!','info','{}','unactioned','woocommerce-admin','2019-11-19 23:15:31',NULL,0),(5,'wc-admin-facebook-extension','info','en_US','Market on Facebook','Grow your business by targeting the right people and driving sales with Facebook. You can install this free extension now.','thumbs-up','{}','actioned','woocommerce-admin','2019-11-19 23:15:31',NULL,0);
